#include <fstream>
#include <string>

int main() {
    std::ifstream inputFile("eilerastis.txt");
    std::ofstream outputFile("eilerastis_kopija.txt");
    std::string line;

    while (getline(inputFile, line)) {
        outputFile << line << '\n';
    }

    inputFile.close();
    outputFile.close();

    return 0;
}
